import { Options } from 'amqplib';
export interface IAmqpClient {
    sendToQueue<TPayload>(queue: string, payload: TPayload, options?: Options.Publish): Promise<void>;
    publish<TPayload>(exchange: string, routingKey?: string, payload?: TPayload, options?: Options.Publish): Promise<void>;
    rpc<TResponse, TPayload>(exchange: string, routingKey: string, payload: TPayload, options?: Options.Publish): Promise<TResponse | undefined>;
    close(): Promise<void>;
}
